"use strict";const configSliderBanners={indice:0,tiempo:8e3};let botonesSlider=["anterior","siguiente"];function establecerYear(){const today=new Date;const nodoYear=document.querySelector(".footer__copy-year");nodoYear.innerHTML=today.getFullYear()}function configurarSlider(nodosElemento,elementoInicial,nodoIndicador,numeroElementos){for(let index=0;index<numeroElementos;index++){if(index!==elementoInicial){nodosElemento.item(index).setAttribute("data-active","false");nodosElemento.item(index).classList.add(`${nodosElemento.item(index).className}--hide`)}else{nodosElemento.item(index).setAttribute("data-active","true")}}crearIndicadores(nodoIndicador,numeroElementos)}function crearIndicadores(nodoIndicador,numeroElementos){for(let indice=0;indice<numeroElementos;indice++){let indicator=document.createElement("div");indicator.classList.add(`${nodoIndicador.className}-item`);indicator.setAttribute("data-indice",String(indice));nodoIndicador.append(indicator)}nodoIndicador.firstElementChild.classList.add(`${nodoIndicador.className}-item--active`)}function mostrarNuevoElemento(tipoBoton,nodosElemento,nodoIndicador,numeroElementos){const indicators=document.querySelectorAll(`.${nodoIndicador.className}-item`);let indiceNuevoElemento=0;let claseNodo="";if(tipoBoton.includes("anterior")){nodosElemento.forEach((elemento,index)=>{if(elemento.getAttribute("data-active")==="true"){claseNodo=`${nodosElemento[index].className}`;elemento.classList.add(`${nodosElemento[index].className}--hide`);elemento.setAttribute("data-active","false");indicators.item(Number(elemento.getAttribute("data-indice"))).classList.remove(`${nodoIndicador.className}-item--active`);if(elemento.getAttribute("data-indice")==="0"){indiceNuevoElemento=numeroElementos-1}else{indiceNuevoElemento=Number(elemento.getAttribute("data-indice"))-1}}})}else if(tipoBoton.includes("siguiente")){nodosElemento.forEach((elemento,index)=>{if(elemento.getAttribute("data-active")==="true"){claseNodo=`${nodosElemento[index].className}`;elemento.classList.add(`${nodosElemento[index].className}--hide`);elemento.setAttribute("data-active","false");indicators.item(Number(elemento.getAttribute("data-indice"))).classList.remove(`${nodoIndicador.className}-item--active`);if(elemento.getAttribute("data-indice")===String(numeroElementos-1)){indiceNuevoElemento=0}else{indiceNuevoElemento=Number(elemento.getAttribute("data-indice"))+1}}})}nodosElemento.item(indiceNuevoElemento).classList.remove(`${claseNodo}--hide`);nodosElemento.item(indiceNuevoElemento).setAttribute("data-active","true");indicators.item(indiceNuevoElemento).classList.add(`${nodoIndicador.className}-item--active`)}establecerYear();const botonPrevSlider=document.querySelector(".slider-banner__button--anterior");const botonNextSlider=document.querySelector(".slider-banner__button--siguiente");const contentBanner=document.querySelectorAll(".banner");const indicatorContainerSlider=document.querySelector(".slider-banner__indicator");if(contentBanner.length>1){configurarSlider(contentBanner,configSliderBanners.indice,indicatorContainerSlider,contentBanner.length);botonPrevSlider.addEventListener("click",event=>{event.preventDefault();mostrarNuevoElemento(botonesSlider[0],contentBanner,indicatorContainerSlider,contentBanner.length)});botonNextSlider.addEventListener("click",event=>{event.preventDefault();mostrarNuevoElemento(botonesSlider[1],contentBanner,indicatorContainerSlider,contentBanner.length)});setInterval(()=>{mostrarNuevoElemento(botonesSlider[1],contentBanner,indicatorContainerSlider,contentBanner.length)},configSliderBanners.tiempo)}else{botonPrevSlider.style.display="none";botonNextSlider.style.display="none";indicatorContainerSlider.style.display="none"}
